import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
  Alert,
} from "react-native";
import Constants from "expo-constants";
import axios from "axios";

export default function HomeScreen() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const apiLink = "https://bloggler-backend.vercel.app/api/post";
  const [offset, setOffset] = useState(0); // Initialize page with 1
  const limit = 10;

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    console.log("fetchdata Called");
    try {
      const response = await axios.get(`${apiLink}?limit=${limit}&offset=${offset}`);
      if (offset === 0) {
        setPosts(response.data.data);
      } else {
        setPosts((prevPosts) => [...prevPosts, ...response.data.data]);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      Alert.alert("Something went wrong!");
      setLoading(false);
    }
  };
  const fetchMore = async () => {
    console.log("FetchMore Called");
    setLoading(true); 
    setOffset(offset + 10); 
    await fetchData();
  };
  const Item = ({ item }) => {
    // console.log("Item data:", item);
    return (
      // <TouchableOpacity>
        <View style={styles.item}>
          <Image
            source={{ uri: item.createdBy.imageUrl }}
            style={styles.icon}
            onError={({ currentTarget }) => {
              currentTarget.onerror = null; // prevents looping
              currentTarget.src="../assets/images/profile.png";
            }}
          />
          <View style={{ flexDirection: "column" }}>
            <Text style={styles.title}>{item.title}</Text>
            <Text style={styles.content}>{item.content}</Text>
          </View>
        </View>
      // </TouchableOpacity>
    );
  };
  // console.log("posts :", posts);

  return (
    <View style={styles.container}>
      <FlatList
        data={posts}
        renderItem={({item}) => <Item key={item._id} item={item} />}
        keyExtractor={(item) => item._id}
        onEndReached={fetchMore}
        onEndReachedThreshold={0.1}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: "white",
    padding: 8,
  },
  item: {
    margin: 10,
    padding: 10,
    flexDirection: "row",
    alignItems: "center",
    borderWidth: 1,
    borderRadius: 10,
  },
  icon: {
    width: 45,
    height: 45,
    marginRight: 10,
    borderRadius: '50%'
  },
  title: {
    fontWeight: "bold",
    fontSize: 16,
  },
  content: {
    fontSize: 14,
  },
});
