import React, { useState } from "react";
import {
  View,
  SafeAreaView,
  TextInput,
  Alert,
  StyleSheet,
  TouchableOpacity,
  onPress,
  Text,
  Image
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from 'axios';

export default function Signup() {

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async () => {
    try {
      const response = await axios.post(
        'https://bloggler-backend.vercel.app/api/user/signup',
        {
          userName:username,
          email:email,
          password:password,
        }
      );
      console.log('token- ',response.data.token);
      if (response.data.token) {
        await AsyncStorage.setItem('token', response.data.token);
      } else {
        Alert.alert('Signup Failed: No token received');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message); // Log the error response or message
      Alert.alert('Something went wrong!');
    }
  };
  


  return (
    <ImageBackground
      source={require('./assets/adaptive-icon.png')}
      style={styles.background}
    >
    
    <SafeAreaView style={styles.container}>
      <View>

      </View>
      <View style={styles.subContainer}> 
      <TextInput
        style={styles.input}
        value={username}
        onChangeText={setUsername}
        placeholder="Username"
      />
      <TextInput
        style={styles.input}
        value={email}
        onChangeText={setEmail}
        placeholder="Email"
        keyboardType="email-address"
      />
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={setPassword}
        placeholder="Password"
        secureTextEntry={true}
      />
      <TouchableOpacity style={styles.button} onPress={handleSubmit}>
        <Text style={{color:'white', fontFamily:'Mona sans'}}>Signup</Text>
      </TouchableOpacity>
      </View>
    </SafeAreaView>
    </ImageBackground>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: "center",
    backgroundColor: "",
    backgroundColor: "#9592C3",
    
    padding: 8,
  },
  subContainer:{
    backgroundColor:'#F3F1EB',
    padding:10,
    marginHorizontal:40,
    borderColor:'black',
    borderWidth:1,
    borderRadius:10,

  },
  paragraph: {
    margin: 24,
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
  input: {
   marginHorizontal:20,
   marginVertical:10,
    borderRadius: 5,
    borderWidth: 1,
    fontSize: 15,
    padding: 5,
    opacity: 0.5,
    fontFamily: "comic sans ms",
    backgroundColor:'#FEFEFE'
  },
  button: {
    alignItems: "center",
    padding: 10,
    width: 100,
    backgroundColor: "#8338ec",
    borderRadius: 50,
    alignSelf: "center",
  },
  background: {
    flex: 1, // This makes sure the image takes the entire screen
    resizeMode: 'cover', // Adjusts the image size to cover the entire area
    justifyContent: 'center', // Aligns children components
  },
});
