import React, { useState } from "react";
import {
  View,
  SafeAreaView,
  TextInput,
  Alert,
  StyleSheet,
  TouchableOpacity,
  onPress,
  Text,
  Image
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from 'axios';

export default function Signup() {

  const [username, setUsername] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = async () => {
    try {
      const response = await axios.post(
        'https://bloggler-backend.vercel.app/api/user/signup',
        {
          userName:username,
          email:email,
          password:password,
        }
      );
      console.log('token- ',response.data.token);
      if (response.data.token) {
        await AsyncStorage.setItem('token', response.data.token);
      } else {
        Alert.alert('Signup Failed: No token received');
      }
    } catch (error) {
      console.error('Error:', error.response?.data || error.message); // Log the error response or message
      Alert.alert('Something went wrong!');
    }
  };
  


  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.title}>
        <Text style={styles.titleText}>Welcome to AirShip</Text>
        <Image source={require('./assets/images/plane.svg')} />
      </View>
      <View style={styles.subContainer}> 
      <Text style={styles.Text}>SIGN UP</Text>
      <Text style={{}}>Create an Account</Text>
      <TextInput
        style={styles.input}
        value={username}
        onChangeText={setUsername}
        placeholder="Username"
      />
      <TextInput
        style={styles.input}
        value={email}
        onChangeText={setEmail}
        placeholder="Email"
        keyboardType="email-address"
      />
      <TextInput
        style={styles.input}
        value={password}
        onChangeText={setPassword}
        placeholder="Password"
        secureTextEntry={true}
      />
      <TouchableOpacity style={styles.button} onPress={handleSubmit}>
        <Text style={{color:'white', fontFamily:'sans serif'}}>Sign Up</Text>
      </TouchableOpacity>
      </View>
      <View style={{padding:100, backgroundColor:'#671d83',marginTop:20}}></View>
    </SafeAreaView>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    // justifyContent: "center",
    backgroundColor: "#d4c7eb",
    padding: 8,
  },
  subContainer:{
    backgroundColor:'#F3F1EB',
    padding:20,
    marginHorizontal:40,
    borderColor:'black',
    borderWidth:1,
    borderRadius:10,
  },
  title:{
    backgroundColor:'#671d83',
    padding:10,
    marginBottom:20,

  },
  Text: {
    margin: 15,
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
  },
  titleText:{
    margin: 25,
    fontSize: 42,
    fontWeight: "bold",
    textAlign: "center",
    color:'#E0F4FF',
    textDecorationStyle:"solid"
  },
  input: {
   marginHorizontal:20,
   marginVertical:10,
    borderRadius: 5,
    borderWidth: 1,
    fontSize: 15,
    padding: 5,
    opacity: 0.8,
    fontFamily: "comic sans ms",
    backgroundColor:'#FEFEFE'
  },
  button: {
    alignItems: "center",
    paddingVertical: 10,
    paddingHorizontal:15,
    width: 100,
    backgroundColor: "#8338ec",
    borderRadius: 50,
    alignSelf: "center",
  },
  background: {
    flex: 1, // This makes sure the image takes the entire screen
    resizeMode: 'cover', // Adjusts the image size to cover the entire area
    justifyContent: 'center', // Aligns children components
  },
});
