import React, { useState } from "react";
import {
  View,
  SafeAreaView,
  TextInput,
  Alert,
  StyleSheet,
  TouchableOpacity,
  Text,
  Image,
  onPress
} from "react-native";
import { useNavigation } from '@react-navigation/native';
import AsyncStorage from "@react-native-async-storage/async-storage";
import axios from "axios";

export default function Login({ }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const navigation = useNavigation();

  const handleSubmit = async () => {
    try {
      const response = await axios.post(
        "https://bloggler-backend.vercel.app/api/user/login",
        {
          email: email,
          password: password,
        }
      );
      console.log("token- ", response.data.access_token);
      if (response.data.access_token) {
        await AsyncStorage.setItem("token", response.data.access_token);
        Alert.alert("Login Successful ✔");
        navigation.navigate("Post");
      } else {
        Alert.alert("Login Failed: No token received");
      }
    } catch (error) {
      console.error("Error:", error.response?.data || error.message); // Log the error response or message
      Alert.alert("Something went wrong!");
    }
  };

  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.title}>
      <TouchableOpacity
          onPress={() => navigation.goBack()} 
        >
          <Image source={require("./assets/images/back.png")} style={styles.back} />
        </TouchableOpacity>
        <Image
          style={styles.image}
          source={require("./assets/images/airship.jpg")}
        />
        <Text style={styles.titleText}>Welcome to AirShip</Text>
      </View>
      <View style={styles.subContainer}>
        <Text style={styles.Text}>LOGIN</Text>
        <TextInput
          style={styles.input}
          value={email}
          onChangeText={setEmail}
          placeholder="email"
        />
        <TextInput
          style={styles.input}
          value={password}
          onChangeText={setPassword}
          placeholder="Password"
          secureTextEntry={true}
        />
        <TouchableOpacity style={{flexDirection:'row-reverse'}} >
          <Text style={{ color: "blue", fontFamily: "comic sans ms", margin:3, }}>
            Forgot Password?
          </Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.button} onPress={handleSubmit}>
          <Text style={{ color: "white", fontFamily: "sans serif" }}>
            Login
          </Text>
        </TouchableOpacity>
      </View>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#d4c7eb",
    padding: 8,
  },
  subContainer: {
    backgroundColor: "#F3F1EB",
    padding: 15,
    marginHorizontal: 30,
    borderColor: "black",
    borderWidth: 1,
    borderRadius: 10,
    width:260,
    height:250,
    alignSelf:'center',
    justifyContent:'space-evenly'
  },
  title: {
    backgroundColor: "#671d83",
    padding: 10,
    marginBottom: 10,
    // flex: 0.7,
    justifyContent:'center',
  },
  Text: {
    margin: 10,
    marginBottom: 2,
    fontSize: 18,
    fontWeight: "bold",
    textAlign: "center",
    textDecorationLine: "underline",
  },
  titleText: {
    margin: 5,
    fontSize: 25,
    fontWeight: "bold",
    textAlign: "center",
    color: "#E0F4FF",
    textDecorationLine: "underline",
    fontFamily: "comic sans ms",
  },
  input: {
    marginHorizontal: 20,
    marginVertical: 5,
    borderRadius: 5,
    borderWidth: 1,
    fontSize: 15,
    padding: 5,
    opacity: 0.8,
    fontFamily: "comic sans ms",
    backgroundColor: "#FEFEFE",
  },
  button: {
    alignItems: "center",
    paddingVertical: 10,
    paddingHorizontal: 15,
    width: 100,
    backgroundColor: "#8338ec",
    borderRadius: 50,
    alignSelf: "center",
  },
  image: {
    height: 140,
    width: 210,
    alignSelf: "center",
    borderRadius: 20,
    opacity: 0.8,
  },
  back: {
    width: 30,
    height: 25,
    padding: 15,
  },
});
