import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
  Alert,
} from "react-native";
import Constants from "expo-constants";
import axios from "axios";
import { SafeAreaView } from "react-native-safe-area-context";

export default function HomeScreen() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const apiLink = "https://bloggler-backend.vercel.app/api/post";
  const [offset, setOffset] = useState(0);
  const limit = 10;

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    console.log("fetchdata Called");
    try {
      const response = await axios.get(`${apiLink}?limit=${limit}&offset=${offset}`);
      if (offset === 0) {
        setPosts(response.data.data);
      } else {
        setPosts((prevPosts) => [...prevPosts, ...response.data.data]);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      Alert.alert("Something went wrong!");
      setLoading(false);
    }
  };
  const fetchMore = async () => {
    console.log("FetchMore Called");
    setLoading(true);
    setOffset(offset + 10);
    await fetchData();
  };
  const Item = ({ item }) => {
    const [profileSource, setProfileSource] = useState(item.createdBy.imageUrl);
    return (
      <SafeAreaView style={styles.post}>
      <View style={styles.item}>
        <Image
          style={styles.icon}
          source={{ uri: profileSource }}
          onError={() => setProfileSource(require('../assets/images/profile.png'))}
        />
        <View style={styles.cardContent}>
          <Text style={styles.title}>
            {item.title.length > 30 ? item.title.substring(0, 30) + "..." : item.title}
          </Text>
          <Text style={styles.content} numberOfLines={5}>
            {item.content}
          </Text>
        </View>
      </View>
      </SafeAreaView>
    );
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={posts}
        renderItem={({ item }) => <Item key={item._id} item={item} />}
        keyExtractor={(item) => item._id}
        onEndReached={fetchMore}
        onEndReachedThreshold={0.1}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: "white",
    padding: 8,
  },
  item: {
    flexDirection: 'row',
    alignItems: 'center',
    borderWidth: 1,
    borderRadius: 10,
    overflow: 'hidden', // Ensure content doesn't overflow the card
    width: '96%', // Make sure each card takes the full width
    padding:10,
    margin:10,
  },
  cardContent: {
    flex: 1,
    paddingHorizontal: 10,
  },
  icon: {
    width: 45,
    height: 45,
    marginRight: 10,
    borderRadius: '50%'
  },
  title: {
    fontWeight: "bold",
    fontSize: 16,
  },
  post:{
    margin :10,
  }
});
