import React, { useState, useEffect } from "react";
import Modal from "react-native-modal";
import {
  Text,
  View,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
  Alert,
  TextInput,
  ScrollView
} from "react-native";
import AsyncStorage from "@react-native-async-storage/async-storage";
import Constants from "expo-constants";
import axios from "axios";
import { SafeAreaView } from "react-native-safe-area-context";
import Post from "./post";

export default function HomeScreen({ navigation }) {
  const [posts, setPosts] = useState([]);
  const apiLink = "https://bloggler-backend.vercel.app/api/post";
  const [offset, setOffset] = useState(0);
  const limit = 10;
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const [isModalVisible, setIsModalVisible] = useState(false);
  const getToken = async () => {
  const token = await AsyncStorage.getItem("token");
  }
  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  }
  const handleLike = () => {

  }
  const handlecomment = () => {

  }

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    console.log("fetchdata Called");
    try {
      const response = await axios.get(`${apiLink}?limit=${limit}&offset=${offset}`);
      if (offset === 0) {
        setPosts(response.data.data);
      } else {
        setPosts((prevPosts) => [...prevPosts, ...response.data.data]);
      }
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      Alert.alert("Something went wrong!");
    }
  };
  const fetchMore = async () => {
    console.log("FetchMore Called");
    setLoading(true);
    setOffset(offset + 10);
    await fetchData();
  };
  const Item = ({ item }) => {
    const [profileSource, setProfileSource] = useState(item.createdBy.imageUrl);
    return (

      <SafeAreaView style={[styles.post, themeStyles.post]}>
        <View style={styles.item}>
          <View style={{ flexDirection: 'row', marginVertical: 5 }}>
            <Image
              style={styles.icon}
              source={{ uri: profileSource }}
              onError={() => setProfileSource(isDarkTheme
                ? require("../assets/images/avatarW.png")
                : require("../assets/images/avatarB.png"))}
            />
            <Text style={styles.userName}>{item.createdBy.userName}</Text>
          </View>
          <View style={styles.cardContent}>
            <Text style={[styles.title, themeStyles.title]}>
              {item.title.length > 30 ? item.title.substring(0, 30) + "..." : item.title}
            </Text>
            <Text style={[styles.content, themeStyles.content]} numberOfLines={5}>
              {item.content}
            </Text>
          </View>
        </View>
        <View style={styles.LikeComment}>
          <TouchableOpacity onPress={handleLike}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/likeW.png")
                  : require("../assets/images/likeD.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
          <TouchableOpacity onPress={handlecomment}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/CommentW.png")
                  : require("../assets/images/CommentB.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
          <TouchableOpacity >
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/shareW.png")
                  : require("../assets/images/shareB.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  };
  const themeStyles = StyleSheet.create({
    container: {
      backgroundColor: isDarkTheme ? "#222" : "#F5EFE7"
    },
    post: {
      backgroundColor: isDarkTheme ? "#6b6b6b" : "white"
    },
    title: {
      color: isDarkTheme ? "#C5FFF8" : "black"
    },
    content: {
      color: isDarkTheme ? "#C5FFF8" : "black"
    }
  });
  const createPost = () => {
    return <Post />;
  }
  return (

    <View style={[styles.container, themeStyles.container]}>
      <View style={styles.header}>
        <Image
          source={
            isDarkTheme
              ? require("../assets/images/logo-white.png")
              : require("../assets/images/logo-black.png")
          }
          style={styles.logo}
        />
        <Image
          style={styles.image}
          source={require("../assets/images/logo.png")}
        />
      </View>
      <ScrollView >
        <View style={styles.headingDesign}>

          <TouchableOpacity
            activeOpacity={0.7}
            onPress={() => setIsModalVisible(true)}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/addW.png")
                  : require("../assets/images/addB.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
          <TextInput style={styles.headingText} placeholder="Search " />
          <TouchableOpacity onPress={toggleTheme}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/sun.png")
                  : require("../assets/images/moon.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>


          <TouchableOpacity 
          onPress={token?(Alert.alert("Login Successful ✔")):()=>{ navigation.navigate("SignUp")}}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/profile white.png")
                  : require("../assets/images/profile black.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>

        </View>

        <FlatList
          data={posts}
          renderItem={({ item }) => <Item key={item._id} item={item} />}
          keyExtractor={(item) => item._id}
          onEndReached={fetchMore}
          onEndReachedThreshold={0.1}
        />

      </ScrollView>
      <Modal visible={isModalVisible} 
      animationType="slide" 
      animationIn={'slideInUp'}
      animationInTiming={500}
      animationOut={'slideOutDown'}
      animationOutTiming={500}
      style={styles.modal}>
        <View style={styles.modalContent}>
          <TouchableOpacity onPress={() => setIsModalVisible(false)}>
            <Text style={{color:'white',fontSize:16, fontWeight:'bold'}}>Close</Text>
          </TouchableOpacity>

          {/* Render the Post component inside the modal */}
          <Post navigation={navigation} />
        </View>
      </Modal>

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: "white",
    padding: 8,
  },
  item: {
    overflow: 'hidden',
    width: '96%',
    padding: 10,
  },
  headingDesign: {
    marginHorizontal: 10,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-evenly',
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
  headingText: {
    backgroundColor: 'white',
    borderRadius: 5,
    paddingHorizontal: 10,
    paddingVertical: 5,
    marginHorizontal: 10,
    borderWidth: 1,
  },
  cardContent: {
    flex: 1,
    paddingHorizontal: 10,
    alignItems: 'center',
  },
  icon: {
    margin: 5,
    width: 20,
    height: 20,
  },
  icon1: {
    margin: 5,
    width: 30,
    height: 30,
    //marginRight:10,
  },
  title: {
    fontWeight: "bold",
    fontSize: 16,
  },
  post: {
    backgroundColor: 'white',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#E5E5E5',
    margin: 8,
    padding: 12,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  LikeComment: {
    flexDirection: 'row',
    marginHorizontal: 10,
    justifyContent: 'space-evenly'
  },
  containerSeparator: {
    padding: 10,
    borderBottomWidth: 1,
    borderBottomColor: '#ddd',
  },
  profile: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 5,
  },
  profileImage: {
    width: 50,
    height: 50,
    borderRadius: 25,
    marginRight: 10,
  },
  logo: {
    width: 50,
    height: 50,
    marginHorizontal: 10,
  },
  header: {
    margin: 5,
    padding: 5,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  image: {
    width: 150,
    height: 50,
    borderRadius: 10,
  },
  userName: {
    fontWeight: 'bold',
    fontSize: 14,
    alignSelf:'center',
  },
  modalContent: {
    backgroundColor: '#454545',
    borderTopLeftRadius: 20,
    borderTopRightRadius: 20,
    paddingHorizontal: 20,
    paddingTop: 20,
    paddingBottom: 40,
  },
  modal: {
    flex: 1,
    justifyContent: 'flex-end',
    margin: 0,
  }
});
