import React, { useState, useEffect } from "react";
import Svg, { G, Path } from 'react-native-svg';
import {
  Text,
  View,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
  Alert,
  TextInput
} from "react-native";
import Constants from "expo-constants";
import axios from "axios";
import { SafeAreaView } from "react-native-safe-area-context";

export default function HomeScreen({ navigation }) {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const apiLink = "https://bloggler-backend.vercel.app/api/post";
  const [offset, setOffset] = useState(0);
  const limit = 10;
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  }
  const handleLike = () => {

  }
  const handlecomment = () => {

  }

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    console.log("fetchdata Called");
    try {
      const response = await axios.get(`${apiLink}?limit=${limit}&offset=${offset}`);
      if (offset === 0) {
        setPosts(response.data.data);
      } else {
        setPosts((prevPosts) => [...prevPosts, ...response.data.data]);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      Alert.alert("Something went wrong!");
      setLoading(false);
    }
  };
  const fetchMore = async () => {
    console.log("FetchMore Called");
    setLoading(true);
    setOffset(offset + 10);
    await fetchData();
  };
  const Item = ({ item }) => {
    const [profileSource, setProfileSource] = useState(item.createdBy.imageUrl);
    return (
      
      <SafeAreaView style={[styles.post, themeStyles.post]}>
        <View style={styles.item}>
          <Image
            style={styles.icon}
            source={{ uri: profileSource }}
            onError={() => setProfileSource(require('../assets/images/profile.png'))}
          />
          <View style={styles.cardContent}>
            <Text style={[styles.title, themeStyles.title]}>
              {item.title.length > 30 ? item.title.substring(0, 30) + "..." : item.title}
            </Text>
            <Text style={[styles.content, themeStyles.content]} numberOfLines={5}>
              {item.content}
            </Text>
          </View>
        </View>
        <View style={styles.LikeComment}>
          <TouchableOpacity onPress={handleLike}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/likeW.png")
                  : require("../assets/images/likeD.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
          <TouchableOpacity onPress={handlecomment}>
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/CommentW.png")
                  : require("../assets/images/CommentB.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
          <TouchableOpacity >
            <Image
              source={
                isDarkTheme
                  ? require("../assets/images/shareW.png")
                  : require("../assets/images/shareB.png")
              }
              style={styles.icon1}
            />
          </TouchableOpacity>
        </View>
      </SafeAreaView>
    );
  };
  const themeStyles = StyleSheet.create({
    container: {
      backgroundColor: isDarkTheme ? "#222" : "white"
    },
    post: {
      backgroundColor: isDarkTheme ? "#6b6b6b" : "white"
    },
    title: {
      color: isDarkTheme ? "white" : "black"
    },
    content: {
      color: isDarkTheme ? "white" : "black"
    }
  });
  return (
    <SafeAreaView style={[styles.container, themeStyles.container]}>
      <View style={styles.headingDesign}>
        <TouchableOpacity onPress={toggleTheme}>
          <Image
            source={
              isDarkTheme
                ? require("../assets/images/sun.png")
                : require("../assets/images/moon.png")
            }
            style={styles.icon1}
          />
        </TouchableOpacity>

        <TextInput style={styles.headingText} placeholder="Search in emails" />
        <TouchableOpacity onPress={toggleTheme}>
          <Image
            source={
              isDarkTheme
                ? require("../assets/images/profile white.png")
                : require("../assets/images/profile black.png")
            }
            style={styles.icon1}
          />
        </TouchableOpacity>
      </View>
      <FlatList
        data={posts}
        renderItem={({ item }) => <Item key={item._id} item={item} />}
        keyExtractor={(item) => item._id}
        onEndReached={fetchMore}
        onEndReachedThreshold={0.1}
      />
      <TouchableOpacity
        activeOpacity={0.7}
        onPress={() => { navigation.navigate("Post"); }}

        style={styles.FloatingActionButtonStyle}>
        <Image
          source={
            isDarkTheme
              ? require("../assets/images/addW.png")
              : require("../assets/images/addB.png")
          }
          style={styles.FloatingActionButtonImageStyle}
        />
      </TouchableOpacity>
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: "white",
    padding: 8,
  },
  item: {
    flexDirection: 'row',
    alignItems: 'center',
    overflow: 'hidden', // Ensure content doesn't overflow the card
    width: '96%', // Make sure each card takes the full width
    padding: 10,

  },
  headingDesign: {
    marginHorizontal: 10,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-evenly',
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
  headingText: {
    backgroundColor: 'white',
    borderRadius: 5,
    paddingHorizontal: 10,
    paddingVertical: 5,
    marginHorizontal: 10,
  },
  cardContent: {
    flex: 1,
    paddingHorizontal: 10,
  },
  icon: {
    width: 45,
    height: 45,
    marginRight: 10,
    borderRadius: '50%'
  },
  title: {
    fontWeight: "bold",
    fontSize: 16,
  },
  post: {
    backgroundColor: 'white',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#E5E5E5',
    margin: 8,
    padding: 12,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  LikeComment: {
    flexDirection: 'row',
    marginHorizontal: 10,
    justifyContent: 'space-evenly'
  },
  icon1: {
    margin: 5,
    width: 20,
    height: 20,
  },
  FloatingActionButtonStyle: {
    position: 'absolute',
    width: 60,
    height: 60,
    alignItems: 'center',
    justifyContent: 'center',
    right: 30,
    bottom: 30,
    backgroundColor:'#0B66D3',
    borderColor:'#000000',
    borderRadius: 200/2
  },

  FloatingActionButtonImageStyle: { 
    width: 30,
    height: 30,
    resizeMode: 'contain',
    tintColor:'#FFFFFF'
  },
});
