import React, { useState, useEffect } from "react";
import Svg, { G, Path } from 'react-native-svg';
import {
  Text,
  View,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
  Alert,
  TextInput
} from "react-native";
import Constants from "expo-constants";
import axios from "axios";
import { SafeAreaView } from "react-native-safe-area-context";

export default function HomeScreen() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const apiLink = "https://bloggler-backend.vercel.app/api/post";
  const [offset, setOffset] = useState(0);
  const limit = 10;
  const [isDarkTheme, setIsDarkTheme] = useState(false);
  const toggleTheme = () => {
    setIsDarkTheme(!isDarkTheme);
  }

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    console.log("fetchdata Called");
    try {
      const response = await axios.get(`${apiLink}?limit=${limit}&offset=${offset}`);
      if (offset === 0) {
        setPosts(response.data.data);
      } else {
        setPosts((prevPosts) => [...prevPosts, ...response.data.data]);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error:", error.response?.data || error.message);
      Alert.alert("Something went wrong!");
      setLoading(false);
    }
  };
  const fetchMore = async () => {
    console.log("FetchMore Called");
    setLoading(true);
    setOffset(offset + 10);
    await fetchData();
  };
  const Item = ({ item }) => {
    const [profileSource, setProfileSource] = useState(item.createdBy.imageUrl);
    return (
      <SafeAreaView style={styles.post}>
        <View style={styles.item}>
          <Image
            style={styles.icon}
            source={{ uri: profileSource }}
            onError={() => setProfileSource(require('../assets/images/profile.png'))}
          />
          <View style={styles.cardContent}>
            <Text style={styles.title}>
              {item.title.length > 30 ? item.title.substring(0, 30) + "..." : item.title}
            </Text>
            <Text style={styles.content} numberOfLines={5}>
              {item.content}
            </Text>
          </View>
        </View>
        <View style={styles.LikeComment}>
          <svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 512 512">
            <path d="M323.8 34.8c-38.2-10.9-78.1 11.2-89 49.4l-5.7 20c-3.7 13-10.4 25-19.5 35l-51.3 56.4c-8.9 9.8-8.2 25 1.6 33.9s25 8.2 33.9-1.6l51.3-56.4c14.1-15.5 24.4-34 30.1-54.1l5.7-20c3.6-12.7 16.9-20.1 29.7-16.5s20.1 16.9 16.5 29.7l-5.7 20c-5.7 19.9-14.7 38.7-26.6 55.5c-5.2 7.3-5.8 16.9-1.7 24.9s12.3 13 21.3 13L448 224c8.8 0 16 7.2 16 16c0 6.8-4.3 12.7-10.4 15c-7.4 2.8-13 9-14.9 16.7s.1 15.8 5.3 21.7c2.5 2.8 4 6.5 4 10.6c0 7.8-5.6 14.3-13 15.7c-8.2 1.6-15.1 7.3-18 15.2s-1.6 16.7 3.6 23.3c2.1 2.7 3.4 6.1 3.4 9.9c0 6.7-4.2 12.6-10.2 14.9c-11.5 4.5-17.7 16.9-14.4 28.8c.4 1.3 .6 2.8 .6 4.3c0 8.8-7.2 16-16 16H286.5c-12.6 0-25-3.7-35.5-10.7l-61.7-41.1c-11-7.4-25.9-4.4-33.3 6.7s-4.4 25.9 6.7 33.3l61.7 41.1c18.4 12.3 40 18.8 62.1 18.8H384c34.7 0 62.9-27.6 64-62c14.6-11.7 24-29.7 24-50c0-4.5-.5-8.8-1.3-13c15.4-11.7 25.3-30.2 25.3-51c0-6.5-1-12.8-2.8-18.7C504.8 273.7 512 257.7 512 240c0-35.3-28.6-64-64-64l-92.3 0c4.7-10.4 8.7-21.2 11.8-32.2l5.7-20c10.9-38.2-11.2-78.1-49.4-89zM32 192c-17.7 0-32 14.3-32 32V448c0 17.7 14.3 32 32 32H96c17.7 0 32-14.3 32-32V224c0-17.7-14.3-32-32-32H32z" />
          </svg>
          <svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 512 512"><path d="M123.6 391.3c12.9-9.4 29.6-11.8 44.6-6.4c26.5 9.6 56.2 15.1 87.8 15.1c124.7 0 208-80.5 208-160s-83.3-160-208-160S48 160.5 48 240c0 32 12.4 62.8 35.7 89.2c8.6 9.7 12.8 22.5 11.8 35.5c-1.4 18.1-5.7 34.7-11.3 49.4c17-7.9 31.1-16.7 39.4-22.7zM21.2 431.9c1.8-2.7 3.5-5.4 5.1-8.1c10-16.6 19.5-38.4 21.4-62.9C17.7 326.8 0 285.1 0 240C0 125.1 114.6 32 256 32s256 93.1 256 208s-114.6 208-256 208c-37.1 0-72.3-6.4-104.1-17.9c-11.9 8.7-31.3 20.6-54.3 30.6c-15.1 6.6-32.3 12.6-50.1 16.1c-.8 .2-1.6 .3-2.4 .5c-4.4 .8-8.7 1.5-13.2 1.9c-.2 0-.5 .1-.7 .1c-5.1 .5-10.2 .8-15.3 .8c-6.5 0-12.3-3.9-14.8-9.9c-2.5-6-1.1-12.8 3.4-17.4c4.1-4.2 7.8-8.7 11.3-13.5c1.7-2.3 3.3-4.6 4.8-6.9c.1-.2 .2-.3 .3-.5z" /></svg>
          <svg xmlns="http://www.w3.org/2000/svg" height="16" width="16" viewBox="0 0 512 512"><path d="M307 34.8c-11.5 5.1-19 16.6-19 29.2v64H176C78.8 128 0 206.8 0 304C0 417.3 81.5 467.9 100.2 478.1c2.5 1.4 5.3 1.9 8.1 1.9c10.9 0 19.7-8.9 19.7-19.7c0-7.5-4.3-14.4-9.8-19.5C108.8 431.9 96 414.4 96 384c0-53 43-96 96-96h96v64c0 12.6 7.4 24.1 19 29.2s25 3 34.4-5.4l160-144c6.7-6.1 10.6-14.7 10.6-23.8s-3.8-17.7-10.6-23.8l-160-144c-9.4-8.5-22.9-10.6-34.4-5.4z" /></svg>
        </View>
      </SafeAreaView>
    );
  };
  const themeStyles = StyleSheet.create({
    container: {
      backgroundColor: isDarkTheme ? "#222" : "white"
    },
    text: {
      color: isDarkTheme ? "white" : "black"
    }
  });
  return (
    <SafeAreaView style={[styles.container, themeStyles.container]}>
      <View style={styles.headingDesign}>
      <TouchableOpacity onPress={toggleTheme}>
      <Image
            source={
              isDarkTheme
                ? require("../assets/images/sun.png")
                : require("../assets/images/")
            }
            style={styles.star}
          />
</TouchableOpacity>
       
        <TextInput style={styles.headingText} placeholder="Search in emails" />
        <svg xmlns="http://www.w3.org/2000/svg" height="16" width="14" viewBox="0 0 448 512"><path d="M224 256A128 128 0 1 0 224 0a128 128 0 1 0 0 256zm-45.7 48C79.8 304 0 383.8 0 482.3C0 498.7 13.3 512 29.7 512H418.3c16.4 0 29.7-13.3 29.7-29.7C448 383.8 368.2 304 269.7 304H178.3z" /></svg>
      </View>
      <FlatList
        data={posts}
        renderItem={({ item }) => <Item key={item._id} item={item} />}
        keyExtractor={(item) => item._id}
        onEndReached={fetchMore}
        onEndReachedThreshold={0.1}
      />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: "white",
    padding: 8,
  },
  item: {
    flexDirection: 'row',
    alignItems: 'center',
    overflow: 'hidden', // Ensure content doesn't overflow the card
    width: '96%', // Make sure each card takes the full width
    padding: 10,

  },
  headingDesign: {
    marginHorizontal:10,
    flexDirection: 'row',
    alignItems: 'center',
    justifyContent: 'space-evenly',
    paddingHorizontal: 10,
    paddingVertical: 5,
  },
  headingText: {
    backgroundColor: 'white',
    borderRadius: 5,
    paddingHorizontal: 10,
    paddingVertical: 5,
    marginHorizontal: 10,
  },
  cardContent: {
    flex: 1,
    paddingHorizontal: 10,
  },
  icon: {
    width: 45,
    height: 45,
    marginRight: 10,
    borderRadius: '50%'
  },
  title: {
    fontWeight: "bold",
    fontSize: 16,
  },
  post: {
    backgroundColor: 'white',
    borderRadius: 10,
    borderWidth: 1,
    borderColor: '#E5E5E5',
    margin: 8,
    padding: 12,
    shadowColor: '#000',
    shadowOffset: {
      width: 0,
      height: 2,
    },
    shadowOpacity: 0.25,
    shadowRadius: 3.84,
    elevation: 5,
  },
  LikeComment: {
    flexDirection: 'row',
    marginHorizontal: 10,
    justifyContent: 'space-evenly'
  },
  icon1: {
    margin: 5,
    width: 30,
    height: 30,
    //marginRight:10,
  },
});
