import React, { useState, useEffect } from "react";
import {
  Text,
  View,
  StyleSheet,
  Image,
  FlatList,
  TouchableOpacity,
  Alert,
} from "react-native";
import Constants from "expo-constants";
import axios from "axios";

export default function HomeScreen() {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get(
        "https://bloggler-backend.vercel.app/api/post?limit=10&userId=651ab3ac600cb0b984bc577d"
      );
      setPosts(response.data)
      setLoading(false);
    } catch (error) {
      console.error("Error:", error.response?.data || error.message); // Log the error response or message
      Alert.alert("Something went wrong!");
      setLoading(false);
    }
  };

  const Item = ({ item }) => {
    return (
      <TouchableOpacity>
        <View style={styles.item}>
          <Image source={{ uri: item.createdBy.imageUrl }} style={styles.icon} />
          <View style={{ flexDirection: "column" }}>
            <Text style={styles.title}>{item.title}</Text>
            <Text style={styles.content}>{item.content}</Text>
          </View>
        </View>
      </TouchableOpacity>
    );
  };

  return (
    <View style={styles.container}>
      {loading ? (
        <Text>Loading...</Text>
      ) : (
        <FlatList
          data={posts}
          renderItem={({ item }) => <Item item={item} />}
          keyExtractor={(item) => item._id}
        />
      )}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    paddingTop: Constants.statusBarHeight,
    backgroundColor: "white",
    padding: 8,
  },
  item: {
    margin: 10,
    marginBottom: 25,
    flexDirection: "row",
    alignItems: "center",
  },
  icon: {
    width: 50,
    height: 45,
    marginRight: 10,
  },
  title: {
    fontWeight: "bold",
    fontSize: 16,
  },
  content: {
    fontSize: 14,
  },
});
