const checkForToken = async () => {
    try {
      const token = await AsyncStorage.getItem('token');
      if (token) {
        // Token exists, navigate to the Home screen
        // You might need to set up your navigation properly here
        // This is a simplified example assuming StackNavigator
        navigation.navigate('Home');
      } else {
        // No token found, navigate to the Login screen
        navigation.navigate('Login');
      }
    } catch (error) {
      // Handle errors, log, or navigate to Login if error occurs
      console.error('Error retrieving token:', error);
      navigation.navigate('Login');
    }
  };
